import React from 'react';
import {TodoCounter} from './TodoCounter';
import {TodoSearch} from './TodoSearch';
import {TodoList} from './TodoList';
import {TodoItem} from './TodoItem';
import {CreateTodoButton} from './CreateTodoButton';

//import './App.css';

const defaultTodos = [
  {text: 'Cortar cebolla', completed: true},
  {text: 'Tomar curso', completed: false},
  {text: 'Correr', completed: false}
];


function App(props) {

  const [todos, setTodos] = React.useState(defaultTodos)
  const [searchValue, setSearchValue] = React.useState('') // esto me permite darle un estado. El array recibe un valor y tambien una funcion para setear el valor

  const completedTodos = todos.filter(todo => !!todo.completed).length;
  const totalTodos = todos.length;

  let searchedTodos = [];

  if(!searchValue.length >= 1) {
    searchedTodos = todos;
  } else {

    searchedTodos = todos.filter( todo => {

      const todoText = todo.text. toLowerCase()

    })
  }


  return (
   <React.Fragment>
      <TodoCounter 
        total={totalTodos}
        completed={completedTodos}
      />
      <TodoSearch 
        searchValue={searchValue}
        setSearchValue={setSearchValue}
      />


      <TodoList>
        {todos.map(todo => (
          <TodoItem 
          key={todo.text} 
          text={todo.text}
          completed={todo.completed}/> 
          //cuando renderizo distintos elementos dentro de un componente, React necesita que le agregue una key a esos componentes, que es la parte que va a cambiar entre uno y otro
        ))}
      </TodoList>

      <CreateTodoButton />

   </React.Fragment>
  );
}

export default App;
